from openpyxl import load_workbookimport win32com.clientfile_path = r'C:\25.develop.home\excel_python_02\test.xlsx'# test.xlsx を読み込むwb = load_workbook(file_path)ws = wb.active# B1を変更してC1をチェックするws['B1'] = 2print(ws['C1'].value)# 閉じるwb.save(file_path)## 単純にMicrosoft Excelを起動してファイルを保存 ここから##app = win32com.client.Dispatch("Excel.Application")app.Visible = Falseapp.DisplayAlerts = Falsewb = app.Workbooks.Open(file_path)wb.Save()app.Quit()## 単純にMicrosoft Excelを起動してファイルを保存 ここまで### data_only=True で開き直すwb = load_workbook(file_path, data_only=True)ws = wb.active# C1をチェックするprint(ws['C1'].value) # Excelの起動/保存がなかったらNoneになる